// Simple test script to verify the API is working
const testData = {
  personImageBase64: "data:image/jpeg;base64,",
  dishImageUrl: "/dish/chakkli.png",
  background: "diwali",
  greeting: "Happy Diwali!"
};

async function testAPI() {
  try {
    console.log('Testing API with data:', testData);
    
    const response = await fetch('https://fortune-image-generator-6i53az4xr-social-beat.vercel.app/api/generate', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(testData)
    });
    
    console.log('Response status:', response.status);
    console.log('Response headers:', Object.fromEntries(response.headers.entries()));
    
    if (!response.ok) {
      const errorText = await response.text();
      console.error('Error response:', errorText);
      return;
    }
    
    const result = await response.json();
    console.log('Success! Result:', result);
    
  } catch (error) {
    console.error('Test failed:', error);
  }
}

testAPI();
